SELECT COUNT(*) as agg_0 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "IMG_REL"."PRED_CLASS_ID" <= 145 AND "IMG_OBJ"."X" >= 151 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" <= 2