SELECT SUM("IMG_OBJ"."H") as agg_0 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "ATT_CLASSES"."ATT_CLASS" LIKE '%a%nd%' AND "PRED_CLASSES"."PRED_CLASS" LIKE '%o%n%' AND "IMG_REL"."IMG_ID" <= 3111173 AND "IMG_OBJ"."H" <= 19