SELECT SUM("OBJ_CLASSES"."OBJ_CLASS_ID" + "IMG_OBJ"."OBJ_SAMPLE_ID") as agg_0, COUNT(*) as agg_1, MAX("IMG_OBJ"."X") as agg_2 FROM "IMG_OBJ" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 9 AND ("IMG_OBJ"."OBJ_CLASS_ID" <= 122 OR "IMG_OBJ"."OBJ_CLASS_ID" BETWEEN 225 AND 313 OR "IMG_OBJ"."OBJ_CLASS_ID" BETWEEN 374 AND 456) AND "OBJ_CLASSES"."OBJ_CLASS" IN ('shelf', 'bowl', 'bus', 'neck', 'train', 'awning', 'reflection', 'headlight')