SELECT MIN("IMG_OBJ"."W") as agg_0, MIN("IMG_OBJ"."X" + "IMG_OBJ"."W") as agg_1, MAX("IMG_OBJ"."Y" + "IMG_OBJ"."OBJ_SAMPLE_ID") as agg_2 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_REL"."OBJ2_SAMPLE_ID" <= 67 AND "IMG_OBJ"."OBJ_SAMPLE_ID" BETWEEN 4 AND 9 AND ("IMG_OBJ"."IMG_ID" BETWEEN 7154499 AND 7171901 OR "IMG_OBJ"."IMG_ID" BETWEEN 4433125 AND 7221510) AND "IMG_REL"."PRED_CLASS_ID" >= 148 AND "IMG_OBJ"."H" BETWEEN 17 AND 40 AND "IMG_REL"."IMG_ID" <= 7196497