SELECT AVG("IMG_OBJ"."OBJ_CLASS_ID") as agg_0, AVG("IMG_OBJ"."H" + "IMG_REL"."OBJ2_SAMPLE_ID") as agg_1 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE "IMG_OBJ"."W" >= 15 AND "ATT_CLASSES"."ATT_CLASS" LIKE '%red%' AND ("IMG_REL"."PRED_CLASS_ID" >= 206 OR "IMG_REL"."OBJ1_SAMPLE_ID" <= 46) AND "ATT_CLASSES"."ATT_CLASS" LIKE '%red%'