SELECT SUM("IMG_REL"."IMG_ID") as agg_0, SUM("IMG_OBJ"."Y") as agg_1, SUM("OBJ_CLASSES"."OBJ_CLASS_ID" + "IMG_REL"."OBJ1_SAMPLE_ID") as agg_2 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID"  WHERE "IMG_OBJ"."H" <= 21