SELECT SUM("PRED_CLASSES"."PRED_CLASS_ID") as agg_0, MAX("PRED_CLASSES"."PRED_CLASS_ID") as agg_1, AVG("PRED_CLASSES"."PRED_CLASS_ID") as agg_2 FROM "PRED_CLASSES"  WHERE "PRED_CLASSES"."PRED_CLASS" NOT LIKE '%fr%ont%' AND "PRED_CLASSES"."PRED_CLASS" NOT LIKE '%are%' AND "PRED_CLASSES"."PRED_CLASS_ID" BETWEEN 34 AND 301