SELECT AVG("IMG_REL"."IMG_ID") as agg_0, MAX("IMG_REL"."OBJ2_SAMPLE_ID" + "IMG_REL"."IMG_ID") as agg_1, SUM("IMG_REL"."OBJ1_SAMPLE_ID") as agg_2 FROM "IMG_REL"  WHERE "IMG_REL"."PRED_CLASS_ID" >= 30 AND "IMG_REL"."IMG_ID" <= 2371932 AND "IMG_REL"."OBJ1_SAMPLE_ID" BETWEEN 23 AND 27 AND "IMG_REL"."OBJ2_SAMPLE_ID" >= 9