SELECT AVG("IMG_REL"."IMG_ID" + "IMG_OBJ"."OBJ_CLASS_ID") as agg_0, MAX("IMG_OBJ"."Y") as agg_1 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "OBJ_CLASSES"."OBJ_CLASS_ID" <= 138 AND "IMG_OBJ"."OBJ_CLASS_ID" <= 206