SELECT AVG("IMG_OBJ"."W" + "IMG_OBJ"."X") as agg_0, MAX("IMG_OBJ"."W") as agg_1, MIN("IMG_OBJ"."W") as agg_2 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID"  WHERE "IMG_OBJ"."Y" >= 264 AND "OBJ_CLASSES"."OBJ_CLASS_ID" BETWEEN 568 AND 882 AND "OBJ_CLASSES"."OBJ_CLASS" != 'laptop'