SELECT AVG("IMG_OBJ"."OBJ_CLASS_ID" + "IMG_OBJ_ATT"."IMG_ID") as agg_0, MIN("IMG_OBJ_ATT"."IMG_ID") as agg_1, MAX("IMG_OBJ"."OBJ_CLASS_ID" + "IMG_OBJ"."W") as agg_2 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE ("IMG_OBJ"."OBJ_CLASS_ID" >= 576 OR "IMG_OBJ"."OBJ_CLASS_ID" BETWEEN 152 AND 192 OR "IMG_OBJ"."OBJ_CLASS_ID" <= 491) AND "IMG_OBJ_ATT"."IMG_ID" BETWEEN 2935083 AND 7160545 AND "ATT_CLASSES"."ATT_CLASS" != 'building' AND ("IMG_OBJ"."X" >= 1244 OR "IMG_OBJ"."OBJ_SAMPLE_ID" >= 1 OR "IMG_OBJ"."X" >= 300)