SELECT SUM("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" + "IMG_REL"."OBJ2_SAMPLE_ID") as agg_0, MIN("IMG_OBJ"."Y" + "IMG_REL"."OBJ1_SAMPLE_ID") as agg_1, SUM("IMG_REL"."PRED_CLASS_ID" + "IMG_REL"."IMG_ID") as agg_2 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "IMG_OBJ_ATT"."ATT_CLASS_ID" >= 638 AND "IMG_REL"."IMG_ID" >= 4780872 AND "PRED_CLASSES"."PRED_CLASS" LIKE '%parked%' AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" <= 1 AND "IMG_OBJ_ATT"."IMG_ID" BETWEEN 2362434 AND 2364911