SELECT MIN("IMG_OBJ_ATT"."OBJ_SAMPLE_ID") as agg_0, AVG("IMG_OBJ"."Y" + "IMG_OBJ"."IMG_ID") as agg_1 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_OBJ"."IMG_ID" <= 6947618 AND "IMG_OBJ_ATT"."ATT_CLASS_ID" >= 1440 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" BETWEEN 4 AND 57 AND "ATT_CLASSES"."ATT_CLASS" NOT LIKE '%dark%'