SELECT MAX("IMG_OBJ"."OBJ_CLASS_ID") as agg_0 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE ("IMG_OBJ"."W" >= 199 OR "IMG_OBJ"."W" <= 932 OR "IMG_OBJ"."W" >= 539) AND "ATT_CLASSES"."ATT_CLASS_ID" <= 1070 AND "IMG_OBJ"."Y" <= 71 AND "ATT_CLASSES"."ATT_CLASS" LIKE '%wh%ite%'