SELECT MAX("IMG_OBJ"."OBJ_SAMPLE_ID") as agg_0, SUM("IMG_OBJ"."H" + "IMG_OBJ"."OBJ_CLASS_ID") as agg_1, SUM("IMG_REL"."PRED_CLASS_ID" + "IMG_OBJ"."IMG_ID") as agg_2 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE ("PRED_CLASSES"."PRED_CLASS" != 'covering' OR ("PRED_CLASSES"."PRED_CLASS" NOT LIKE '%hanging%' AND "PRED_CLASSES"."PRED_CLASS" IN ('playing on', 'painted on', 'made of', 'against', 'from'))) AND ("IMG_OBJ"."W" <= 47 OR "IMG_OBJ"."W" BETWEEN 117 AND 123 OR "IMG_OBJ"."W" <= -4)