SELECT SUM("IMG_OBJ_ATT"."IMG_ID") as agg_0 FROM "IMG_OBJ" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE ("IMG_OBJ"."X" BETWEEN 81 AND 190 OR ("IMG_OBJ"."H" <= 0 AND "IMG_OBJ"."X" BETWEEN 189 AND 293)) AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" <= 13 AND ("IMG_REL"."PRED_CLASS_ID" BETWEEN 149 AND 432 OR "IMG_REL"."OBJ2_SAMPLE_ID" <= 10) AND "ATT_CLASSES"."ATT_CLASS" = 'yellow' AND "IMG_REL"."IMG_ID" >= 2671555 AND "IMG_OBJ_ATT"."IMG_ID" >= 2741067