SELECT MIN("IMG_OBJ_ATT"."OBJ_SAMPLE_ID") as agg_0, AVG("IMG_OBJ"."X") as agg_1 FROM "IMG_OBJ" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "IMG_OBJ_ATT"."IMG_ID" BETWEEN 2345315 AND 2959443 AND "IMG_OBJ_ATT"."ATT_CLASS_ID" <= 1225