SELECT AVG("IMG_REL"."OBJ2_SAMPLE_ID" + "IMG_REL"."OBJ1_SAMPLE_ID") as agg_0, MIN("IMG_OBJ"."X" + "IMG_OBJ"."Y") as agg_1, SUM("IMG_OBJ"."Y" + "IMG_OBJ"."IMG_ID") as agg_2 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "IMG_REL"."PRED_CLASS_ID" <= 145 OR ("IMG_REL"."IMG_ID" BETWEEN 2385041 AND 2393697 AND "IMG_REL"."PRED_CLASS_ID" BETWEEN 140 AND 246)