SELECT AVG("IMG_OBJ"."H") as agg_0, AVG("IMG_OBJ"."Y" + "IMG_REL"."PRED_CLASS_ID") as agg_1 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE "ATT_CLASSES"."ATT_CLASS" IN ('dark colored', 'rusted', 'seated', 'evergreen', 'outdoors', 'airborne') AND ("IMG_OBJ_ATT"."IMG_ID" <= 3567278 OR ("IMG_OBJ_ATT"."IMG_ID" >= 2406632 AND "IMG_OBJ_ATT"."IMG_ID" <= 4774341)) AND "IMG_REL"."OBJ2_SAMPLE_ID" >= 2 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 1