SELECT SUM("IMG_OBJ_ATT"."IMG_ID" + "IMG_OBJ_ATT"."ATT_CLASS_ID") as agg_0, MIN("PRED_CLASSES"."PRED_CLASS_ID" + "IMG_REL"."OBJ2_SAMPLE_ID") as agg_1, AVG("IMG_OBJ"."X") as agg_2 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID"  WHERE ("IMG_OBJ"."OBJ_CLASS_ID" >= 208 OR "IMG_OBJ"."OBJ_SAMPLE_ID" BETWEEN 3 AND 6 OR "IMG_OBJ"."OBJ_CLASS_ID" <= 470) AND "IMG_REL"."OBJ1_SAMPLE_ID" >= 17