SELECT MAX("IMG_OBJ"."IMG_ID" + "IMG_OBJ"."H") as agg_0, COUNT(*) as agg_1, MAX("OBJ_CLASSES"."OBJ_CLASS_ID" + "IMG_OBJ"."H") as agg_2 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "ATT_CLASSES"."ATT_CLASS" IN ('building s', 'outdoors', 'rusted', 'seated', 'dark colored') AND ("OBJ_CLASSES"."OBJ_CLASS_ID" BETWEEN 508 AND 761 OR "OBJ_CLASSES"."OBJ_CLASS" = 'guys' OR "OBJ_CLASSES"."OBJ_CLASS_ID" >= 286) AND "ATT_CLASSES"."ATT_CLASS" IN ('outdoors', 'airborne') AND "IMG_OBJ"."OBJ_CLASS_ID" >= 292 AND "IMG_OBJ_ATT"."ATT_CLASS_ID" <= 697