SELECT SUM("IMG_OBJ"."H" + "IMG_OBJ_ATT"."IMG_ID") as agg_0, COUNT(*) as agg_1, AVG("IMG_OBJ"."W") as agg_2 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "ATT_CLASSES"."ATT_CLASS" != 'diced' AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 1 AND ("IMG_OBJ"."OBJ_SAMPLE_ID" <= 6 OR ("IMG_OBJ"."OBJ_SAMPLE_ID" BETWEEN 6 AND 7 AND "IMG_OBJ"."OBJ_SAMPLE_ID" >= 1))