SELECT AVG("IMG_REL"."OBJ1_SAMPLE_ID") as agg_0, MAX("IMG_REL"."PRED_CLASS_ID") as agg_1 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID"  WHERE "PRED_CLASSES"."PRED_CLASS_ID" <= 121 AND "IMG_REL"."OBJ2_SAMPLE_ID" BETWEEN 6 AND 13 AND "IMG_REL"."OBJ1_SAMPLE_ID" <= 4