SELECT MIN("IMG_REL"."PRED_CLASS_ID") as agg_0, MIN("PRED_CLASSES"."PRED_CLASS_ID") as agg_1, AVG("PRED_CLASSES"."PRED_CLASS_ID" + "IMG_REL"."IMG_ID") as agg_2 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID"  WHERE "IMG_REL"."OBJ2_SAMPLE_ID" BETWEEN 1 AND 13 AND ("PRED_CLASSES"."PRED_CLASS_ID" >= 247 OR "PRED_CLASSES"."PRED_CLASS" IN ('against', 'to', 'looking at', 'made of', 'from') OR "PRED_CLASSES"."PRED_CLASS_ID" BETWEEN 240 AND 446) AND "IMG_REL"."PRED_CLASS_ID" BETWEEN 417 AND 441