SELECT SUM("IMG_REL"."IMG_ID") as agg_0 FROM "IMG_OBJ" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE ("IMG_OBJ_ATT"."ATT_CLASS_ID" >= 562 OR "IMG_OBJ_ATT"."ATT_CLASS_ID" BETWEEN 696 AND 956) AND "PRED_CLASSES"."PRED_CLASS" NOT LIKE '%fro%m%' AND "IMG_REL"."OBJ2_SAMPLE_ID" >= 10 AND "OBJ_CLASSES"."OBJ_CLASS_ID" >= 823 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 7 AND "IMG_OBJ"."H" BETWEEN 46 AND 57