SELECT MIN("IMG_REL"."IMG_ID") as agg_0, MIN("IMG_REL"."PRED_CLASS_ID" + "IMG_REL"."OBJ1_SAMPLE_ID") as agg_1, SUM("IMG_REL"."OBJ2_SAMPLE_ID" + "IMG_REL"."PRED_CLASS_ID") as agg_2 FROM "IMG_REL"  WHERE "IMG_REL"."OBJ2_SAMPLE_ID" <= 10 AND "IMG_REL"."IMG_ID" >= 3019357 AND "IMG_REL"."OBJ1_SAMPLE_ID" <= 2 AND "IMG_REL"."PRED_CLASS_ID" BETWEEN 193 AND 410