SELECT SUM("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" + "IMG_REL"."PRED_CLASS_ID") as agg_0, MAX("IMG_OBJ"."X" + "IMG_REL"."OBJ2_SAMPLE_ID") as agg_1 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "OBJ_CLASSES"."OBJ_CLASS_ID" >= 110 AND "IMG_OBJ"."Y" >= 101 AND "IMG_REL"."OBJ2_SAMPLE_ID" <= 12 AND "IMG_OBJ_ATT"."ATT_CLASS_ID" BETWEEN 684 AND 2092