SELECT SUM("IMG_OBJ"."H" + "IMG_OBJ"."W") as agg_0, AVG("IMG_OBJ"."H") as agg_1, COUNT(*) as agg_2 FROM "IMG_OBJ" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE ("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 2 OR "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 9 OR "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" BETWEEN 2 AND 12) AND "IMG_OBJ"."Y" <= 210 AND "IMG_OBJ"."IMG_ID" <= 7176179