SELECT MAX("IMG_OBJ_ATT"."ATT_CLASS_ID" + "IMG_OBJ_ATT"."IMG_ID") as agg_0, MAX("IMG_OBJ_ATT"."IMG_ID") as agg_1, MAX("IMG_OBJ_ATT"."OBJ_SAMPLE_ID") as agg_2 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "ATT_CLASSES"."ATT_CLASS" LIKE '%b%lue%' OR ("ATT_CLASSES"."ATT_CLASS" IN ('oval', 'dark colored', 'upside down', 'building s', 'outdoors', 'airborne', 'seated', 'vertical', 'evergreen') AND "ATT_CLASSES"."ATT_CLASS" NOT LIKE '%red%')