SELECT MIN("IMG_REL"."OBJ2_SAMPLE_ID") as agg_0, MIN("IMG_REL"."IMG_ID") as agg_1, SUM("IMG_OBJ"."W" + "IMG_OBJ"."IMG_ID") as agg_2 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE "ATT_CLASSES"."ATT_CLASS" NOT LIKE '%dark%' AND ("IMG_OBJ_ATT"."IMG_ID" <= 6487984 OR ("IMG_OBJ_ATT"."ATT_CLASS_ID" <= 1930 AND "IMG_OBJ_ATT"."IMG_ID" >= 2365362))