SELECT SUM("IMG_OBJ"."IMG_ID" + "IMG_OBJ"."Y") as agg_0, MAX("IMG_OBJ"."W") as agg_1 FROM "IMG_OBJ" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "OBJ_CLASSES"."OBJ_CLASS_ID" >= 626 AND "OBJ_CLASSES"."OBJ_CLASS" != 'lettering' AND ("IMG_OBJ"."IMG_ID" BETWEEN 7207021 AND 7212829 OR "IMG_OBJ"."Y" BETWEEN 238 AND 308)