SELECT MIN("PRED_CLASSES"."PRED_CLASS_ID" + "IMG_REL"."OBJ2_SAMPLE_ID") as agg_0, MAX("IMG_REL"."OBJ1_SAMPLE_ID") as agg_1 FROM "IMG_REL" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID"  WHERE "PRED_CLASSES"."PRED_CLASS" NOT LIKE '%to%' AND "IMG_REL"."IMG_ID" BETWEEN 2370966 AND 2392731 AND "IMG_REL"."OBJ2_SAMPLE_ID" BETWEEN 2 AND 19 AND ("PRED_CLASSES"."PRED_CLASS_ID" >= 390 OR ("PRED_CLASSES"."PRED_CLASS_ID" <= 110 AND "PRED_CLASSES"."PRED_CLASS_ID" BETWEEN 229 AND 257))