SELECT AVG("IMG_OBJ"."Y") as agg_0, MIN("IMG_OBJ"."OBJ_CLASS_ID") as agg_1, SUM("IMG_REL"."PRED_CLASS_ID" + "IMG_OBJ"."OBJ_SAMPLE_ID") as agg_2 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID"  WHERE "IMG_OBJ"."IMG_ID" >= 5045415 AND "PRED_CLASSES"."PRED_CLASS" = 'has' AND "IMG_OBJ"."OBJ_SAMPLE_ID" >= 9 AND "PRED_CLASSES"."PRED_CLASS_ID" BETWEEN 42 AND 87