SELECT AVG("IMG_OBJ"."IMG_ID" + "IMG_REL"."OBJ1_SAMPLE_ID") as agg_0, SUM("IMG_OBJ"."X") as agg_1 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "PRED_CLASSES"."PRED_CLASS" != 'using' AND "IMG_OBJ"."W" BETWEEN 8 AND 30