SELECT SUM("OBJ_CLASSES"."OBJ_CLASS_ID" + "IMG_OBJ_ATT"."IMG_ID") as agg_0, MIN("IMG_OBJ_ATT"."OBJ_SAMPLE_ID") as agg_1 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "ATT_CLASSES"."ATT_CLASS_ID" BETWEEN 286 AND 1571