SELECT SUM("IMG_OBJ"."IMG_ID" + "IMG_REL"."OBJ2_SAMPLE_ID") as agg_0 FROM "IMG_OBJ" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "IMG_REL"."OBJ2_SAMPLE_ID" >= 2 AND "OBJ_CLASSES"."OBJ_CLASS" IN ('neck', 'bus', 'trunk', 'awning', 'headlight', 'bowl', 'reflection', 'shelf', 'train') AND "IMG_OBJ"."IMG_ID" <= 4793635