SELECT AVG("IMG_REL"."IMG_ID" + "IMG_REL"."PRED_CLASS_ID") as agg_0, MAX("IMG_REL"."OBJ2_SAMPLE_ID") as agg_1, SUM("IMG_REL"."PRED_CLASS_ID" + "IMG_REL"."OBJ1_SAMPLE_ID") as agg_2 FROM "IMG_REL"  WHERE "IMG_REL"."IMG_ID" BETWEEN 7183581 AND 7190669 AND ("IMG_REL"."OBJ2_SAMPLE_ID" >= 2 OR "IMG_REL"."OBJ2_SAMPLE_ID" >= 3 OR "IMG_REL"."OBJ2_SAMPLE_ID" <= 1)