SELECT SUM("IMG_OBJ"."H" + "IMG_OBJ"."OBJ_SAMPLE_ID") as agg_0, COUNT(*) as agg_1 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "ATT_CLASSES"."ATT_CLASS_ID" BETWEEN 852 AND 1270 AND "IMG_OBJ_ATT"."IMG_ID" BETWEEN 2728325 AND 7209703 AND "ATT_CLASSES"."ATT_CLASS" = 'red'