SELECT SUM("PRED_CLASSES"."PRED_CLASS_ID") as agg_0, MAX("IMG_OBJ"."X" + "IMG_REL"."IMG_ID") as agg_1 FROM "IMG_REL" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "PRED_CLASSES"."PRED_CLASS" != 'drinking' OR "PRED_CLASSES"."PRED_CLASS" != 'drinking'