SELECT MAX("IMG_OBJ"."W") as agg_0, MIN("IMG_OBJ"."OBJ_CLASS_ID" + "OBJ_CLASSES"."OBJ_CLASS_ID") as agg_1, MIN("IMG_REL"."PRED_CLASS_ID" + "IMG_OBJ"."X") as agg_2 FROM "IMG_OBJ" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "IMG_OBJ"."W" BETWEEN 30 AND 846 AND "OBJ_CLASSES"."OBJ_CLASS" IN ('headlight', 'reflection', 'neck', 'bowl', 'shelf', 'awning', 'edge', 'bus') AND "IMG_REL"."IMG_ID" BETWEEN 994062 AND 2808915 AND "IMG_REL"."OBJ1_SAMPLE_ID" >= 11 AND "IMG_OBJ"."OBJ_CLASS_ID" >= 517