SELECT SUM("IMG_OBJ"."X" + "IMG_OBJ"."IMG_ID") as agg_0 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "PRED_CLASSES"."PRED_CLASS_ID" >= 343 AND "IMG_OBJ"."OBJ_SAMPLE_ID" BETWEEN 5 AND 6