SELECT COUNT(*) as agg_0, MIN("IMG_OBJ"."W" + "IMG_OBJ_ATT"."ATT_CLASS_ID") as agg_1 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_OBJ"."Y" <= 215 AND "IMG_OBJ"."OBJ_CLASS_ID" >= 370 AND "ATT_CLASSES"."ATT_CLASS" IN ('outdoors')