SELECT SUM("PRED_CLASSES"."PRED_CLASS_ID") as agg_0, MAX("PRED_CLASSES"."PRED_CLASS_ID") as agg_1, AVG("PRED_CLASSES"."PRED_CLASS_ID") as agg_2 FROM "PRED_CLASSES"  WHERE ("PRED_CLASSES"."PRED_CLASS_ID" <= 260 OR "PRED_CLASSES"."PRED_CLASS_ID" BETWEEN 169 AND 221 OR "PRED_CLASSES"."PRED_CLASS_ID" >= 283) AND "PRED_CLASSES"."PRED_CLASS" NOT LIKE '%gr%owing%'