SELECT MAX("IMG_OBJ"."H") as agg_0, COUNT(*) as agg_1, AVG("IMG_OBJ"."Y" + "IMG_OBJ_ATT"."IMG_ID") as agg_2 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_OBJ_ATT"."ATT_CLASS_ID" BETWEEN 636 AND 2090 AND "IMG_OBJ"."IMG_ID" >= 7243872 AND "IMG_OBJ"."OBJ_SAMPLE_ID" BETWEEN 35 AND 47 AND "ATT_CLASSES"."ATT_CLASS_ID" >= 321 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" BETWEEN 3 AND 17