SELECT AVG("IMG_OBJ"."X" + "IMG_OBJ"."IMG_ID") as agg_0, MAX("IMG_OBJ"."OBJ_SAMPLE_ID") as agg_1, MIN("IMG_OBJ"."OBJ_CLASS_ID" + "IMG_OBJ"."OBJ_SAMPLE_ID") as agg_2 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID"  WHERE ("OBJ_CLASSES"."OBJ_CLASS_ID" BETWEEN 129 AND 684 OR ("OBJ_CLASSES"."OBJ_CLASS_ID" BETWEEN 365 AND 489 AND "OBJ_CLASSES"."OBJ_CLASS_ID" BETWEEN 42 AND 888)) AND ("IMG_OBJ"."OBJ_CLASS_ID" >= 235 OR ("IMG_OBJ"."H" BETWEEN 18 AND 38 AND "IMG_OBJ"."OBJ_CLASS_ID" <= 522)) AND "IMG_OBJ"."X" <= 195 AND "IMG_OBJ"."W" BETWEEN 86 AND 150