SELECT AVG("IMG_REL"."OBJ2_SAMPLE_ID" + "IMG_OBJ"."Y") as agg_0, COUNT(*) as agg_1 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE "OBJ_CLASSES"."OBJ_CLASS" IN ('headlight', 'trunk', 'train', 'reflection', 'edge', 'bus', 'shelf', 'neck') AND "OBJ_CLASSES"."OBJ_CLASS_ID" BETWEEN 390 AND 744