SELECT MAX("IMG_OBJ"."W") as agg_0, AVG("IMG_OBJ"."OBJ_SAMPLE_ID") as agg_1, MAX("IMG_OBJ"."OBJ_CLASS_ID" + "OBJ_CLASSES"."OBJ_CLASS_ID") as agg_2 FROM "IMG_OBJ" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" BETWEEN 7 AND 27 AND "IMG_OBJ"."H" >= 135 AND "IMG_OBJ"."OBJ_SAMPLE_ID" BETWEEN 9 AND 10 AND "OBJ_CLASSES"."OBJ_CLASS_ID" >= 355