SELECT SUM("IMG_REL"."OBJ1_SAMPLE_ID" + "IMG_REL"."OBJ2_SAMPLE_ID") as agg_0 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "IMG_OBJ"."IMG_ID" <= 7208388 AND "ATT_CLASSES"."ATT_CLASS" LIKE '%light%' AND ("IMG_OBJ"."OBJ_CLASS_ID" BETWEEN 292 AND 530 OR ("IMG_OBJ"."H" >= 1117 AND "IMG_OBJ"."OBJ_CLASS_ID" >= 45)) AND ("IMG_OBJ"."Y" >= 248 OR "IMG_OBJ"."Y" BETWEEN 165 AND 307) AND ("IMG_REL"."OBJ2_SAMPLE_ID" BETWEEN 9 AND 18 OR "IMG_REL"."OBJ2_SAMPLE_ID" >= 2 OR "IMG_REL"."OBJ2_SAMPLE_ID" >= 2)