SELECT MIN("PRED_CLASSES"."PRED_CLASS_ID" + "IMG_OBJ"."W") as agg_0 FROM "IMG_REL" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "IMG_OBJ"."OBJ_CLASS_ID" BETWEEN 198 AND 867