SELECT COUNT(*) as agg_0, SUM("IMG_OBJ_ATT"."IMG_ID") as agg_1 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE ("IMG_OBJ"."X" BETWEEN 21 AND 246 OR "IMG_OBJ"."X" BETWEEN 0 AND 363) AND "IMG_OBJ_ATT"."ATT_CLASS_ID" <= 1377 AND ("PRED_CLASSES"."PRED_CLASS_ID" <= 39 OR "PRED_CLASSES"."PRED_CLASS" NOT LIKE '%t%op%') AND "OBJ_CLASSES"."OBJ_CLASS" IN ('shelf', 'bowl', 'train', 'edge', 'headlight')